
<div class="horizontal_bar"><div class="horizontal_bar_outline <?=$onclick && $id != '' ? 'clickable' : ''?>" <?=$onclick && $id != '' ? 'onmousemove="if(mouseStatus) updateGraphHorizontalBar(event, \''.$id.'\')" onmouseup="updateGraphHorizontalBar(event, \''.$id.'\'); '.$onclick.'" onmousedown="updateGraphHorizontalBar(event, \''.$id.'\'); '.$onclick.'"' : ''?>><span class="horizontal_bar_fill" <?=$id != '' ? 'id="'.$id.'_bar"' : ''?> style="<?=$color?>width: <?=$percentage + 0?>%"></span></div><? if($show_percentage):?>&nbsp;<div class="horizontal_bar_value" <?=$id != '' ? 'id="'.$id.'_value"' : ''?>><?=h($percentage)?>%</div><? endif?></div>
